import{createRepoCard}from"./repository-card.js";const PER_PAGE=10,form=document.querySelector(".form-search"),input=document.querySelector("input"),repositoriesContainer=document.querySelector(".repositories");form.addEventListener("submit",(async e=>{e.preventDefault();const r=await fetch(`https://api.github.com/search/repositories?q=${input.value}&per_page=10`);try{const e=await r.json();if(input.value="",repositoriesContainer.innerHTML="",!e.items.length)throw new Error("Ничего не найдено");const t=e.items.map((e=>createRepoCard(e))).join("");repositoriesContainer.innerHTML=t}catch(e){alert(`Ошибка! ${e}`)}}));