import{createRepoCard}from"./repository-card.js";const PER_PAGE=10,form=document.querySelector(".form-search"),input=document.querySelector("input"),repositoriesContainer=document.querySelector(".repositories"),emptyMessage='<span class="empty-message">Nothing found</span>';form.addEventListener("submit",(async e=>{e.preventDefault();const t=await fetch(`https://api.github.com/search/repositories?q=${input.value}&per_page=10`);try{const e=await t.json();if(input.value="",repositoriesContainer.innerHTML="",!e.items.length)throw new Error;const r=e.items.map((e=>createRepoCard(e))).join("");repositoriesContainer.innerHTML=r}catch{repositoriesContainer.innerHTML=emptyMessage}}));